mapper.select:
	
	
	
	
	
         select rn, noticeNum, noticeTitle, noticeContent, postDate, updateDate from (
             select rownum as rn, noticeNum, noticeTitle, noticeContent, postDate, updateDate 
             from notice where rownum <= #{pageNum} * #{amount}
             
           
		
			
		
              
             
             )
              where rn > (#{pageNum} -1) * #{amount}

mapper.update:
update notice set noticeTitle = #{noticeTitle},
noticeContent=#{noticeContent}, updateDate = sysdate where noticeNum =
#{noticeNum}
